mapper.update:

   update users 
   set follower_num = (select count(follow_users_no)  from follow where users_no=#{users_no}) 
   where users_no = #{users_no}

mapper.delete:

   delete 
      from follow
   where users_no = #{users_no} and follow_users_no=#{authUser_no}

mapper.select:

   select users_no
      from follow
      where users_no=#{users_no} and follow_users_no = #{authUser_no}


   select count(follow_users_no)
      from follow
      where users_no=#{users_no}
