mapper.select:

select users_no, nickname
  from users
 where email = #{email}


select users_no, email, nickname, pass_word, sec_pass_word, info, users_image, follower_num, follower_num, infomation
  from users
 where users_no = #{users_no }


select users_no, email, pass_word, nickname, infomation, users_image, follower_num, following_num,  description
	from users
where email = #{email} and pass_word = #{pass_word};


select users_no 
	from users
where email = #{email};


select users_no 
	from users 
where nickname = #{nickname};


	select email 
		from users 
			where email=#{email};


	select users_no
		from users
		where email=#{email} and pass_word =#{pass_word}

mapper.update:

	update users 
		set sec_pass_word=#{sec_pass_word} 
			where nickname=#{nickname};


update users
	set nickname = #{nickname}, infomation = #{infomation}
		where users_no=#{users_no};
